import React, {Component} from "react";

class Student extends Component {
    constructor(props) {
        super(props);
        this.state = {
            number: 0
        }
    }

    render() {
        return (
            <div>
                <button>delete all</button>
                {
                    this.props.students.map((student, index) => {
                        let image = ''
                        const path = '..'+student.avatar
                        const avatar =  require(path.split(''))
                        try {
                            image = require(path)
                        } catch (e) {
                            console.log('could not load ' + student.avatar)
                        }
                        return (
                            <div className="student" key={student.name + index}>
                                <h1>Student {index + 1}</h1>
                                <p>name: {student.name}</p>
                                <p>age: {student.age}</p>
                                <p>birthday: {student.birthday}</p>
                                <img src={avatar} alt="Could not load image"/>
                                <p>address: {student.address}</p>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

export default Student
